mapper.select:
SELECT * FROM ( SELECT @ROWNUM := @ROWNUM+1 AS no, k.PLAN_CODE AS plan_code, k.DPT_NAME AS dpt_name, k.CUST_NAME AS cust_name, convert(date_format(k.STARTDATE, '%Y-%m-%d'), char(10)) as startDate, convert(date_format(k.ENDDATE, '%Y-%m-%d'), char(10)) as endDate, convert(date_format(k.REG_DATE, '%Y-%m-%d'), char(10)) as reg_date, k.PLANQTY AS planQty, k.PLAN_STATUS AS plan_status, case k.cnt when 1 then k.pdt_name_one else concat(k.pdt_name_one, ' 외', (k.cnt-1), '건') end AS pdt_name FROM ( select A.PLAN_CODE, F.CUST_NAME as CUST_NAME, IFNULL(D.DPT_NAME,'') as DPT_NAME, A.STARTDATE, A.ENDDATE, A.REG_DATE, A.PLAN_STATUS, ( SELECT SUM(F.PLANQTY) FROM producePlan_detail F WHERE F.plan_code = A.plan_code) as PLANQTY, B.pdt_name as pdt_name_one , ( SELECT COUNT(*) FROM producePlan_detail F WHERE F.plan_code = A.plan_code) AS cnt , ( SELECT MAX(PDT_NAME) FROM producePlan_detail F WHERE F.plan_code = A.plan_code) AS pdt from producePlan A inner join producePlan_detail B on A.plan_code = B.plan_code left outer join mes_department D on A.dpt_code = D.dpt_code inner join cust F on A.cust_seq = F.cust_seq ,(SELECT @ROWNUM:=0) R where B.no = 1 AND A.DELETE_YN = 'N' ${sQuery}
AND (A.REG_DATE >= #{startDate} and A.STARTDATE <= #{endDate})
order by A.reg_date desc ) k )sub WHERE sub.no BETWEEN ((#{currentPage}-1) * #{rowsPerPage}) + 1 AND #{currentPage} * #{rowsPerPage}

mapper.update:
UPDATE produceplan
	SET PLAN_STATUS = #{plan_status}
		,MOD_BY =  #{iManagerSeq}
WHERE
	PLAN_CODE = #{plan_code}
